		CPackageBarChart::CPackageBarChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Core/BarChartRenderer.h");
			AddHeaderFile(L"Charts/Controls/BarChart/BarChart.h");
		}

		IControl *CPackageBarChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CBarChart() };
			if (properties)
				*properties = new CBarChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CBarChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageBarChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CBarChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageBarChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CBarChartEventList(getPackage(), control, this);
			return nullptr;
		}

