		CPackageDoughnutChart::CPackageDoughnutChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/DoughnutChart/DoughnutChart.h");
		}

		IControl *CPackageDoughnutChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CDoughnutChart() };
			if (properties)
				*properties = new CDoughnutChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CDoughnutChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageDoughnutChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CDoughnutChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageDoughnutChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CDoughnutChartEventList(getPackage(), control, this);
			return nullptr;
		}

