		CPackageForexChart::CPackageForexChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/ForexChart/ForexChart.h");
		}

		IControl *CPackageForexChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CForexChart() };
			if (properties)
				*properties = new CForexChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CForexChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageForexChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CForexChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageForexChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CForexChartEventList(getPackage(), control, this);
			return nullptr;
		}

