		CPackageLineChart::CPackageLineChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/LineChart/LineChart.h");
		}

		IControl *CPackageLineChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CLineChart() };
			if (properties)
				*properties = new CLineChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CLineChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageLineChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CLineChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageLineChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CLineChartEventList(getPackage(), control, this);
			return nullptr;
		}

