		CPackageMultiBarChart::CPackageMultiBarChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/MultiBarChart/MultiBarChart.h");
		}

		IControl *CPackageMultiBarChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CMultiBarChart() };
			if (properties)
				*properties = new CMultiBarChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CMultiBarChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageMultiBarChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CMultiBarChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageMultiBarChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CMultiBarChartEventList(getPackage(), control, this);
			return nullptr;
		}

