		CPackagePieChart::CPackagePieChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/PieChart/PieChart.h");
		}

		IControl *CPackagePieChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPieChart() };
			if (properties)
				*properties = new CPieChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPieChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePieChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPieChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePieChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPieChartEventList(getPackage(), control, this);
			return nullptr;
		}

