		CPackagePolarAreaChart::CPackagePolarAreaChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/PolarAreaChart/PolarAreaChart.h");
		}

		IControl *CPackagePolarAreaChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPolarAreaChart() };
			if (properties)
				*properties = new CPolarAreaChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPolarAreaChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePolarAreaChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPolarAreaChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePolarAreaChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPolarAreaChartEventList(getPackage(), control, this);
			return nullptr;
		}

