		CPackageSemiPieChart::CPackageSemiPieChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/SemiPieChart/SemiPieChart.h");
		}

		IControl *CPackageSemiPieChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CSemiPieChart() };
			if (properties)
				*properties = new CSemiPieChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSemiPieChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSemiPieChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CSemiPieChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageSemiPieChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CSemiPieChartEventList(getPackage(), control, this);
			return nullptr;
		}

