		CPackageSplineChart::CPackageSplineChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/SplineChart/SplineChart.h");
		}

		IControl *CPackageSplineChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CSplineChart() };
			if (properties)
				*properties = new CSplineChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSplineChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSplineChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CSplineChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageSplineChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CSplineChartEventList(getPackage(), control, this);
			return nullptr;
		}

