		CPackageSteppedLineChart::CPackageSteppedLineChart(IPackage *package) : CPackageControl(package, L"charts"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Charts/Controls/SteppedLineChart/SteppedLineChart.h");
		}

		IControl *CPackageSteppedLineChart::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CSteppedLineChart() };
			if (properties)
				*properties = new CSteppedLineChartPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSteppedLineChartEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSteppedLineChart::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CSteppedLineChartPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageSteppedLineChart::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CSteppedLineChartEventList(getPackage(), control, this);
			return nullptr;
		}

