		CPackageFormEditor::CPackageFormEditor(IPackage *package) : CPackageControl(package, L"ide"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Ide/Controls/FormEditor/FormEditor.h");
		}

		IControl *CPackageFormEditor::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CFormEditor() };
			if (properties)
				*properties = new CFormEditorPropertyList(getPackage(), result, this);
			if (events)
				*events = new CFormEditorEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageFormEditor::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CFormEditorPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageFormEditor::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CFormEditorEventList(getPackage(), control, this);
			return nullptr;
		}

