		{
			return L"FormView";
		}

		String CPackageFormView::getReferenceUrl()
		{
			return L"";
		}

		const Image *CPackageFormView::getIcon()
		{
			return &m_cIcon;
		}

		bool CPackageFormView::isVisible()
		{
			return true;
		}

		bool CPackageFormView::isDisplayIcon()
		{
			return false;
		}

		bool CPackageFormView::isInstance(IControl *control)
		{
			return cast<CFormView*>(control) != nullptr;
		}

		bool CPackageFormView::isTransformable()
		{
			return true;
		}
