		CPackageViewCross::CPackageViewCross(IPackage *package) : CPackageControl(package, L"ide"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Ide/Controls/ViewCross/ViewCross.h");
		}

		IControl *CPackageViewCross::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewCross() };
			if (properties)
				*properties = new CViewCrossPropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewCrossEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewCross::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewCrossPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewCross::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewCrossEventList(getPackage(), control, this);
			return nullptr;
		}

