		IControl *CPackageViewH1H2L::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH1H2L() };
			if (properties)
				*properties = new CViewH1H2LPropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH1H2LEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewH1H2L::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH1H2LPropertyList(getPackage(), control, this);
			return nullptr;
		}

