		IControl *CPackageViewH1H2R::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH1H2R() };
			if (properties)
				*properties = new CViewH1H2RPropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH1H2REventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

