		IControl *CPackageViewH1H3::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH1H3() };
			if (properties)
				*properties = new CViewH1H3PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH1H3EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewH1H3::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH1H3PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewH1H3::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH1H3EventList(getPackage(), control, this);
			return nullptr;
		}

