		CPackageViewH2::CPackageViewH2(IPackage *package) : CPackageControl(package, L"ide"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Ide/Controls/ViewH2/ViewH2.h");
		}

		IControl *CPackageViewH2::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH2() };
			if (properties)
				*properties = new CViewH2PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH2EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewH2::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH2PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewH2::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH2EventList(getPackage(), control, this);
			return nullptr;
		}

