		IControl *CPackageViewH2H1::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH2H1() };
			if (properties)
				*properties = new CViewH2H1PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH2H1EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewH2H1::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH2H1PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewH2H1::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH2H1EventList(getPackage(), control, this);
			return nullptr;
		}

