		IControl *CPackageViewH2H2::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH2H2() };
			if (properties)
				*properties = new CViewH2H2PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH2H2EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewH2H2::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH2H2PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewH2H2::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH2H2EventList(getPackage(), control, this);
			return nullptr;
		}

