		IControl *CPackageViewH2RH1::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH2RH1() };
			if (properties)
				*properties = new CViewH2RH1PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH2RH1EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

