		CPackageViewH3::CPackageViewH3(IPackage *package) : CPackageControl(package, L"ide"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Ide/Controls/ViewH3/ViewH3.h");
		}

		IControl *CPackageViewH3::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewH3() };
			if (properties)
				*properties = new CViewH3PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewH3EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewH3::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH3PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewH3::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewH3EventList(getPackage(), control, this);
			return nullptr;
		}

