		CPackageViewV2::CPackageViewV2(IPackage *package) : CPackageControl(package, L"ide"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Ide/Controls/ViewV2/ViewV2.h");
		}

		IControl *CPackageViewV2::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewV2() };
			if (properties)
				*properties = new CViewV2PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewV2EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewV2::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewV2PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewV2::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewV2EventList(getPackage(), control, this);
			return nullptr;
		}

