		IControl *CPackageViewV2V1::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewV2V1() };
			if (properties)
				*properties = new CViewV2V1PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewV2V1EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewV2V1::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewV2V1PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewV2V1::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewV2V1EventList(getPackage(), control, this);
			return nullptr;
		}

