		CPackageViewV3::CPackageViewV3(IPackage *package) : CPackageControl(package, L"ide"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Ide/Controls/ViewV3/ViewV3.h");
		}

		IControl *CPackageViewV3::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CViewV3() };
			if (properties)
				*properties = new CViewV3PropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewV3EventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageViewV3::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewV3PropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageViewV3::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewV3EventList(getPackage(), control, this);
			return nullptr;
		}

