		CPackageBox::CPackageBox(IPackage *package) : CPackage3DControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/Box/Box.h");
		}

		IControl *CPackageBox::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CBox() };
			if (properties)
				*properties = new CBoxPropertyList(getPackage(), result, this);
			if (events)
				*events = new CBoxEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageBox::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CBoxPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageBox::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CBoxEventList(getPackage(), control, this);
			return nullptr;
		}

