		{
			IControl *result{ new CCheckerMaterial() };
			if (properties)
				*properties = new CCheckerMaterialPropertyList(getPackage(), result, this);
			if (events)
				*events = new CCheckerMaterialEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageCheckerMaterial::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CCheckerMaterialPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageCheckerMaterial::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CCheckerMaterialEventList(getPackage(), control, this);
			return nullptr;
		}

