		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/Circle/Circle.h");
		}

		IControl *CPackageCircle::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CCircle() };
			if (properties)
				*properties = new CCirclePropertyList(getPackage(), result, this);
			if (events)
				*events = new CCircleEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageCircle::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CCirclePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageCircle::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CCircleEventList(getPackage(), control, this);
			return nullptr;
		}

