		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/CustomMaterial/CustomMaterial.h");
		}

		IControl *CPackageCustomMaterial::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CCustomMaterial() };
			if (properties)
				*properties = new CCustomMaterialPropertyList(getPackage(), result, this);
			if (events)
				*events = new CCustomMaterialEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageCustomMaterial::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CCustomMaterialPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageCustomMaterial::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CCustomMaterialEventList(getPackage(), control, this);
			return nullptr;
		}

