		IControl *CPackageGouraudMaterial::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CGouraudMaterial() };
			if (properties)
				*properties = new CGouraudMaterialPropertyList(getPackage(), result, this);
			if (events)
				*events = new CGouraudMaterialEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageGouraudMaterial::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CGouraudMaterialPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageGouraudMaterial::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CGouraudMaterialEventList(getPackage(), control, this);
			return nullptr;
		}

