		CPackageHeightmap::CPackageHeightmap(IPackage *package) : CPackage3DControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/Heightmap/Heightmap.h");
		}

		IControl *CPackageHeightmap::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CHeightmap() };
			if (properties)
				*properties = new CHeightmapPropertyList(getPackage(), result, this);
			if (events)
				*events = new CHeightmapEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageHeightmap::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CHeightmapPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageHeightmap::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CHeightmapEventList(getPackage(), control, this);
			return nullptr;
		}

