		CPackageJsonMesh::CPackageJsonMesh(IPackage *package) : CPackage3DControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/JsonMesh/JsonMesh.h");
		}

		IControl *CPackageJsonMesh::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CJsonMesh() };
			if (properties)
				*properties = new CJsonMeshPropertyList(getPackage(), result, this);
			if (events)
				*events = new CJsonMeshEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageJsonMesh::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CJsonMeshPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageJsonMesh::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CJsonMeshEventList(getPackage(), control, this);
			return nullptr;
		}

		String CPackageJsonMesh::getCategory()
		{
			return L"3D";
		}

