		IControl *CPackagePhongMaterial::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPhongMaterial() };
			if (properties)
				*properties = new CPhongMaterialPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPhongMaterialEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePhongMaterial::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPhongMaterialPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePhongMaterial::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPhongMaterialEventList(getPackage(), control, this);
			return nullptr;
		}

