		CPackagePlane::CPackagePlane(IPackage *package) : CPackage3DControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/Plane/Plane.h");
		}

		IControl *CPackagePlane::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPlane() };
			if (properties)
				*properties = new CPlanePropertyList(getPackage(), result, this);
			if (events)
				*events = new CPlaneEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePlane::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPlanePropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackagePlane::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CPlaneEventList(getPackage(), control, this);
			return nullptr;
		}

