		CPackagePyramid::CPackagePyramid(IPackage *package) : CPackage3DControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/Pyramid/Pyramid.h");
		}

		IControl *CPackagePyramid::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CPyramid() };
			if (properties)
				*properties = new CPyramidPropertyList(getPackage(), result, this);
			if (events)
				*events = new CPyramidEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackagePyramid::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CPyramidPropertyList(getPackage(), control, this);
			return nullptr;
		}

