		CPackageRimMaterial::CPackageRimMaterial(IPackage *package) : CPackageMaterialControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/RimMaterial/RimMaterial.h");
		}

		IControl *CPackageRimMaterial::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CRimMaterial() };
			if (properties)
				*properties = new CRimMaterialPropertyList(getPackage(), result, this);
			if (events)
				*events = new CRimMaterialEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageRimMaterial::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CRimMaterialPropertyList(getPackage(), control, this);
			return nullptr;
		}

