		CPackageTorus::CPackageTorus(IPackage *package) : CPackage3DControl(package), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"OpenGL/Controls/Torus/Torus.h");
		}

		IControl *CPackageTorus::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CTorus() };
			if (properties)
				*properties = new CTorusPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTorusEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTorus::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CTorusPropertyList(getPackage(), control, this);
			return nullptr;
		}

