		CPackageView::CPackageView(IPackage *package) : CPackageControl(package, L"opengl"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms
			AddPlatform(PlatformVersion{ PlatformType::Windows, L"", L"" });

			// Header files
			AddHeaderFile(L"OpenGL/Controls/View/View.h");
		}

		IControl *CPackageView::Create(IPropertyList **properties, IEventList **events)
		{
			IControl *result{ new CView() };
			if (properties)
				*properties = new CViewPropertyList(getPackage(), result, this);
			if (events)
				*events = new CViewEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateControl(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageView::CreatePropertyList(IControl *control)
		{
			if (isInstance(control))
				return new CViewPropertyList(getPackage(), control, this);
			return nullptr;
		}

		IEventList *CPackageView::CreateEventList(IControl *control)
		{
			if (isInstance(control))
				return new CViewEventList(getPackage(), control, this);
			return nullptr;
		}

