		CBitBtn::State CBitBtn::getState()
		{
			if (!isEnabled(false))
				return State::Disabled;
			if (m_bKeyDown || m_bMouseDown)
			{
				if (isHovered())
					return State::ActiveHovered;
				return State::Active;
			}
			if (isFocused())
			{
				if (isHovered())
					return State::FocusedHovered;
				return State::Focused;
			}
			if (isHovered())
				return State::Hovered;
			return State::Normal;
		}

