		CPackageAdminRights::CPackageAdminRights(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms
			AddPlatform(PlatformVersion{ PlatformType::Windows, L"", L"" });

			// Header files
			AddHeaderFile(L"Standard/Components/AdminRights/AdminRights.h");
		}

		IComponent *CPackageAdminRights::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CAdminRights() };
			if (properties)
				*properties = new CAdminRightsPropertyList(getPackage(), result, this);
			if (events)
				*events = new CAdminRightsEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageAdminRights::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CAdminRightsPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageAdminRights::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CAdminRightsEventList(getPackage(), component, this);
			return nullptr;
		}

