		CPackageColorDialog::CPackageColorDialog(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/ColorDialog/ColorDialog.h");
		}

		IComponent *CPackageColorDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CColorDialog() };
			if (properties)
				*properties = new CColorDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CColorDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageColorDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CColorDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageColorDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CColorDialogEventList(getPackage(), component, this);
			return nullptr;
		}

