		CPackageFindDialog::CPackageFindDialog(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/FindDialog/FindDialog.h");
		}

		IComponent *CPackageFindDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CFindDialog() };
			if (properties)
				*properties = new CFindDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CFindDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageFindDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CFindDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageFindDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CFindDialogEventList(getPackage(), component, this);
			return nullptr;
		}

