		CPackageImageList::CPackageImageList(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/ImageList/ImageList.h");
		}

		IComponent *CPackageImageList::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CImageList() };
			if (properties)
				*properties = new CImageListPropertyList(getPackage(), result, this);
			if (events)
				*events = new CImageListEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageImageList::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CImageListPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageImageList::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CImageListEventList(getPackage(), component, this);
			return nullptr;
		}

