		CPackageReplaceDialog::CPackageReplaceDialog(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/ReplaceDialog/ReplaceDialog.h");
		}

		IComponent *CPackageReplaceDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CReplaceDialog() };
			if (properties)
				*properties = new CReplaceDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CReplaceDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageReplaceDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CReplaceDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageReplaceDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CReplaceDialogEventList(getPackage(), component, this);
			return nullptr;
		}

