		CPackageSimpleXML::CPackageSimpleXML(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/SimpleXML/SimpleXML.h");
		}

		IComponent *CPackageSimpleXML::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CSimpleXML() };
			if (properties)
				*properties = new CSimpleXMLPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSimpleXMLEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSimpleXML::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CSimpleXMLPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageSimpleXML::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CSimpleXMLEventList(getPackage(), component, this);
			return nullptr;
		}

