		CPackageSysColorDialog::CPackageSysColorDialog(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/SysColorDialog/SysColorDialog.h");
		}

		IComponent *CPackageSysColorDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CSysColorDialog() };
			if (properties)
				*properties = new CSysColorDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSysColorDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSysColorDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysColorDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageSysColorDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysColorDialogEventList(getPackage(), component, this);
			return nullptr;
		}

