		IComponent *CPackageSysFontDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CSysFontDialog() };
			if (properties)
				*properties = new CSysFontDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSysFontDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSysFontDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysFontDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageSysFontDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysFontDialogEventList(getPackage(), component, this);
			return nullptr;
		}

