		CPackageSysOpenDialog::CPackageSysOpenDialog(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Supported platforms

			// Header files
			AddHeaderFile(L"Standard/Components/SysOpenDialog/SysOpenDialog.h");
		}

		IComponent *CPackageSysOpenDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CSysOpenDialog() };
			if (properties)
				*properties = new CSysOpenDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSysOpenDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSysOpenDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysOpenDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageSysOpenDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysOpenDialogEventList(getPackage(), component, this);
			return nullptr;
		}

