		IComponent *CPackageSysOpenPictureDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CSysOpenPictureDialog() };
			if (properties)
				*properties = new CSysOpenPictureDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSysOpenPictureDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSysOpenPictureDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysOpenPictureDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageSysOpenPictureDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysOpenPictureDialogEventList(getPackage(), component, this);
			return nullptr;
		}

