		IComponent *CPackageSysSaveDialog::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CSysSaveDialog() };
			if (properties)
				*properties = new CSysSaveDialogPropertyList(getPackage(), result, this);
			if (events)
				*events = new CSysSaveDialogEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageSysSaveDialog::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysSaveDialogPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageSysSaveDialog::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CSysSaveDialogEventList(getPackage(), component, this);
			return nullptr;
		}

