		CPackageTCPServer::CPackageTCPServer(IPackage *package) : CPackageComponent(package, L"standard"), m_cIcon{ IconWidth, IconHeight, IconData }
		{
			// Header files
			AddHeaderFile(L"Standard/Components/TCPServer/TCPServer.h");
		}

		IComponent *CPackageTCPServer::Create(IPropertyList **properties, IEventList **events)
		{
			IComponent *result{ new CTCPServer() };
			if (properties)
				*properties = new CTCPServerPropertyList(getPackage(), result, this);
			if (events)
				*events = new CTCPServerEventList(getPackage(), result, this);
			if (properties || events)
				getPackage()->QueryService()->NotifyOnCreateComponent(result, this, *properties, *events);
			return result;
		}

		IPropertyList *CPackageTCPServer::CreatePropertyList(IComponent *component)
		{
			if (isInstance(component))
				return new CTCPServerPropertyList(getPackage(), component, this);
			return nullptr;
		}

		IEventList *CPackageTCPServer::CreateEventList(IComponent *component)
		{
			if (isInstance(component))
				return new CTCPServerEventList(getPackage(), component, this);
			return nullptr;
		}

		String CPackageTCPServer::getCategory()
		{
			return L"Internet";
		}

